import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import SummaryTable from './SummaryTable';
import { fetchDataFromSheet } from '../utils/DataFetcher';
import { calculateWeeklySummary, calculateAllSummary, calculateTotalRow } from '../utils/Calculations';
import './GoogleSheetData.css';

// Your component logic here...

const GoogleSheetData = () => {
  const [summaryData, setSummaryData] = useState([]);
  const [weeklySummaryData, setWeeklySummaryData] = useState([]);
  const [allSummaryData, setAllSummaryData] = useState([]);
  const [dailyPachiraIncome, setDailyPachiraIncome] = useState([]);
  const [weeklyPachiraIncome, setWeeklyPachiraIncome] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: 'date', direction: 'ascending' });
  const [view, setView] = useState('daily'); // Track current view (daily, weekly, all, dailyIncome, weeklyIncome)

  const sheetId = '1D2B33Uj1vS_weRZQAQdMRcaQbk1KG1SAjNfoaHAKrSo'; // Your Google Sheet ID
  const apiKey = 'AIzaSyBAPhvHyE-PbFUktWwlt-2_KJfwQTabEvQ'; // Your API Key

  // Memoize the tabNames array to prevent re-creation on each render
  const tabNames = useMemo(() => ['S1x', 'S2x', 'S3PM', 'S3IR', 'follow', 'Tennis', 'MLB', 'American Football'], []);

  // Your component logic, functions, and useEffects go here...

  // Return statement with your JSX structure...
  return (
    <div>
      <h1>Performance Summary</h1>
      <div>
        <button onClick={() => setView('daily')}>Daily Summary</button>
        <button onClick={() => setView('weekly')}>Weekly Summary</button>
        <button onClick={() => setView('all')}>All Summary</button>
        <button onClick={() => setView('dailyIncome')}>Daily Pachira Income</button>
        <button onClick={() => setView('weeklyIncome')}>Weekly Pachira Income</button>
      </div>
      {view === 'daily' ? (
        <>
          <h2>Daily Summary</h2>
          <SummaryTable data={summaryData} sortConfig={sortConfig} sortData={sortData} tabNames={tabNames} isWeekly={false} isAllSummary={false} />
        </>
      ) : view === 'weekly' ? (
        <>
          <h2>Weekly Summary</h2>
          <SummaryTable data={weeklySummaryData} sortConfig={sortConfig} sortData={sortData} tabNames={tabNames} isWeekly={true} isAllSummary={false} />
        </>
      ) : view === 'all' ? (
        <>
          <h2>All Summary</h2>
          <SummaryTable data={allSummaryData} sortConfig={sortConfig} sortData={sortData} tabNames={tabNames} isWeekly={false} isAllSummary={true} />
        </>
      ) : view === 'dailyIncome' ? (
        <>
          <h2>Daily Pachira Income</h2>
          <SummaryTable data={dailyPachiraIncome} sortConfig={sortConfig} sortData={sortData} tabNames={[]} isWeekly={false} isAllSummary={false} isIncome={true} />
        </>
      ) : view === 'weeklyIncome' ? (
        <>
          <h2>Weekly Pachira Income</h2>
          <SummaryTable data={weeklyPachiraIncome} sortConfig={sortConfig} sortData={sortData} tabNames={[]} isWeekly={true} isAllSummary={false} isIncome={true} />
        </>
      ) : null}
    </div>
  );
};

// Ensure that this export statement is at the very end of your file
export default GoogleSheetData;
