// src/components/GoogleSheetData.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GoogleSheetData = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const sheetId = '1D2B33Uj1vS_weRZQAQdMRcaQbk1KG1SAjNfoaHAKrSo'; // Your Google Sheet ID
  const apiKey = 'AIzaSyBAPhvHyE-PbFUktWwlt-2_KJfwQTabEvQ'; // Your API Key
  const tabNames = ['S1', 'S2', 'S3PM', 'S3IR', 'follow']; // List of your sheet tabs

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        // Fetch data from all tabs
        const tabDataPromises = tabNames.map((tab) =>
          axios.get(`https://sheets.googleapis.com/v4/spreadsheets/${sheetId}/values/${tab}?key=${apiKey}`)
        );

        // Wait for all promises to resolve
        const responses = await Promise.all(tabDataPromises);

        // Combine all data into a single array
        const allData = responses.reduce((acc, response, index) => {
          const tabData = response.data.values || []; // Handle if no data in tab
          return [...acc, { tabName: tabNames[index], tabData }];
        }, []);

        setData(allData);
      } catch (error) {
        setError('Error fetching data');
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Google Sheet Data</h1>
      {data.map((tab, tabIndex) => (
        <div key={tabIndex}>
          <h2>{tab.tabName}</h2>
          <table border="1">
            <thead>
              <tr>
                {tab.tabData[0] ? tab.tabData[0].map((header, index) => (
                  <th key={index}>{header}</th>
                )) : <th>No Data</th>}
              </tr>
            </thead>
            <tbody>
              {tab.tabData.slice(1).map((row, rowIndex) => (
                <tr key={rowIndex}>
                  {row.map((cell, cellIndex) => (
                    <td key={cellIndex}>{cell}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default GoogleSheetData;
