import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import './GoogleSheetData.css'; // Import CSS for conditional formatting

const GoogleSheetData = () => {
  const [summaryData, setSummaryData] = useState([]);
  const [weeklySummaryData, setWeeklySummaryData] = useState([]);
  const [allSummaryData, setAllSummaryData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: 'date', direction: 'ascending' });
  const [view, setView] = useState('daily'); // Track current view (daily, weekly, all)

  const sheetId = '1D2B33Uj1vS_weRZQAQdMRcaQbk1KG1SAjNfoaHAKrSo'; // Your Google Sheet ID
  const apiKey = 'AIzaSyBAPhvHyE-PbFUktWwlt-2_KJfwQTabEvQ'; // Your API Key

  // Memoize the tabNames array to prevent re-creation on each render
  const tabNames = useMemo(() => ['S1x', 'S2x', 'S3PM', 'S3IR', 'follow'], []);

  const plColumnIndex = 8; // Index for the P/L column (Column I)

  // Function to calculate weekly summary
  const calculateWeeklySummary = useCallback((dailyData) => {
    const weeklySummary = {};

    dailyData.forEach(row => {
      // Validate date format and handle invalid dates
      const dateParts = row.date.split('/');
      if (dateParts.length !== 3) {
        console.warn(`Skipping invalid date format: ${row.date}`);
        return; // Skip rows with invalid date format
      }

      const [day, month, year] = dateParts.map(Number);
      if (isNaN(day) || isNaN(month) || isNaN(year)) {
        console.warn(`Skipping invalid date values: ${row.date}`);
        return; // Skip rows with non-numeric date parts
      }

      const dateObj = new Date(year, month - 1, day); // Create date object (month is 0-based in JavaScript)
      if (isNaN(dateObj.getTime())) {
        console.warn(`Skipping invalid date object: ${row.date}`);
        return; // Skip rows where the date object is invalid
      }

      const weekStart = new Date(dateObj.setDate(dateObj.getDate() - dateObj.getDay() + 1)); // Get Monday of the week
      const weekKey = weekStart.toISOString().split('T')[0]; // Use ISO string as key for grouping

      if (!weeklySummary[weekKey]) {
        weeklySummary[weekKey] = { weekStart: weekKey };
      }

      tabNames.forEach(tab => {
        weeklySummary[weekKey][tab] = (weeklySummary[weekKey][tab] || 0) + (row[tab] || 0);
      });

      weeklySummary[weekKey].Total = (weeklySummary[weekKey].Total || 0) + row.Total;
    });

    const weeklyArray = Object.values(weeklySummary);
    setWeeklySummaryData(weeklyArray);
  }, [tabNames]);

  // Function to calculate All Summary
  const calculateAllSummary = useCallback((dailyData) => {
    const totalSummary = {};

    dailyData.forEach(row => {
      tabNames.forEach(tab => {
        totalSummary[tab] = (totalSummary[tab] || 0) + (row[tab] || 0);
      });
    });

    const summaryArray = Object.entries(totalSummary).map(([tab, totalPL]) => ({
      tabName: tab,
      totalPL
    }));

    setAllSummaryData(summaryArray);
  }, [tabNames]);

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        // Fetch data from all tabs
        const tabDataPromises = tabNames.map((tab) => {
          const apiUrl = `https://sheets.googleapis.com/v4/spreadsheets/${sheetId}/values/${tab}?key=${apiKey}`;
          console.log(`Fetching data for ${tab} from URL: ${apiUrl}`);
          return axios.get(apiUrl)
            .then(response => ({ tabName: tab, data: response.data }))
            .catch(error => {
              console.error(`Error fetching data for ${tab}:`, error);
              return { tabName: tab, data: null, error };
            });
        });

        // Wait for all promises to resolve
        const tabDataResults = await Promise.all(tabDataPromises);

        // Combine all data into a summary object
        const summary = {};

        tabDataResults.forEach(({ tabName, data, error }) => {
          if (data === null) {
            console.error(`No data received for ${tabName}. Error:`, error?.response ? error.response.data : error.message);
            return;
          }

          const tabData = data.values || [];

          // Debugging: Log the first few rows to check structure
          console.log(`Data for ${tabName}:`, tabData.slice(0, 5));

          // Check if there is data in the tab
          if (tabData.length < 2) {
            console.warn(`No data found in tab ${tabName}`);
          } else {
            console.log(`Total rows in ${tabName}:`, tabData.length);
          }

          // Skip the header row and process each row
          tabData.slice(1).forEach((row, rowIndex) => {
            const date = row[0]; // Date column

            // Check if P/L column exists and has a valid number
            if (row[plColumnIndex] !== undefined) {
              // Remove commas and parse the number
              const plRaw = row[plColumnIndex].replace(/,/g, '').trim();
              const pl = parseFloat(plRaw);

              if (!isNaN(pl)) {
                if (!summary[date]) {
                  summary[date] = { date };
                }
                summary[date][tabName] = (summary[date][tabName] || 0) + pl;

                // Debugging: Log each row processing
                console.log(`Processing row ${rowIndex + 1} for ${tabName} on ${date}:`, row, `Parsed P/L: ${pl}`);
              } else {
                console.warn(`Invalid P/L value at row ${rowIndex + 1} for ${tabName} on ${date}:`, row[plColumnIndex]);
              }
            } else {
              console.warn(`Missing P/L value at row ${rowIndex + 1} for ${tabName} on ${date}`);
            }
          });
        });

        // Convert summary object to array and calculate total
        const summaryArray = Object.values(summary).map(entry => {
          const total = tabNames.reduce((acc, tab) => acc + (entry[tab] || 0), 0);
          return { ...entry, Total: total };
        });

        setSummaryData(summaryArray);
        calculateWeeklySummary(summaryArray); // Calculate weekly summary
        calculateAllSummary(summaryArray); // Calculate All summary
      } catch (error) {
        setError('Error fetching data');
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
  }, [apiKey, sheetId, tabNames, calculateWeeklySummary, calculateAllSummary]); // Added missing dependencies

  const sortData = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }

    const dataToSort = view === 'daily' ? summaryData : view === 'weekly' ? weeklySummaryData : allSummaryData;

    const sortedData = [...dataToSort].sort((a, b) => {
      if (key === 'date' || key === 'weekStart') {
        // For date or weekStart, convert to Date object and compare
        return direction === 'ascending'
          ? new Date(a[key].split('/').reverse().join('-')) - new Date(b[key].split('/').reverse().join('-'))
          : new Date(b[key].split('/').reverse().join('-')) - new Date(a[key].split('/').reverse().join('-'));
      } else {
        // For other columns, compare numerical values
        const aValue = a[key] !== undefined ? a[key] : 0;
        const bValue = b[key] !== undefined ? b[key] : 0;
        return direction === 'ascending' ? aValue - bValue : bValue - aValue;
      }
    });

    if (view === 'daily') {
      setSummaryData(sortedData);
    } else if (view === 'weekly') {
      setWeeklySummaryData(sortedData);
        } else {
      setAllSummaryData(sortedData);
    }

    setSortConfig({ key, direction });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  // Function to render different tables based on view type
  const renderTable = (data, isWeekly, isAllSummary) => (
    <table border="1">
      <thead>
        <tr>
          {isAllSummary ? (
            <>
              <th onClick={() => sortData('tabName')}>
                Tab {sortConfig.key === 'tabName' ? (sortConfig.direction === 'ascending' ? '↑' : '↓') : ''}
              </th>
              <th onClick={() => sortData('totalPL')}>
                Total P/L {sortConfig.key === 'totalPL' ? (sortConfig.direction === 'ascending' ? '↑' : '↓') : ''}
              </th>
            </>
          ) : (
            <>
              <th onClick={() => sortData(isWeekly ? 'weekStart' : 'date')}>
                {isWeekly ? 'Week Starting' : 'Date'} {sortConfig.key === (isWeekly ? 'weekStart' : 'date') ? (sortConfig.direction === 'ascending' ? '↑' : '↓') : ''}
              </th>
              {tabNames.map((tab, index) => (
                <th key={index} onClick={() => sortData(tab)}>
                  {tab} {sortConfig.key === tab ? (sortConfig.direction === 'ascending' ? '↑' : '↓') : ''}
                </th>
              ))}
              <th onClick={() => sortData('Total')}>
                Total {sortConfig.key === 'Total' ? (sortConfig.direction === 'ascending' ? '↑' : '↓') : ''}
              </th>
            </>
          )}
        </tr>
      </thead>
      <tbody>
        {data.map((row, rowIndex) => (
          <tr key={rowIndex}>
            {isAllSummary ? (
              <>
                <td>{row.tabName}</td>
                <td className={row.totalPL > 0 ? 'positive' : row.totalPL < 0 ? 'negative' : ''}>
                  {Math.round(row.totalPL)}
                </td>
              </>
            ) : (
              <>
                <td>{isWeekly ? row.weekStart : row.date}</td>
                {tabNames.map((tab, cellIndex) => (
                  <td
                    key={cellIndex}
                    className={row[tab] > 0 ? 'positive' : row[tab] < 0 ? 'negative' : ''}
                  >
                    {row[tab] !== undefined ? Math.round(row[tab]) : '-'}
                  </td>
                ))}
                <td className={row.Total > 0 ? 'positive' : row.Total < 0 ? 'negative' : ''}>
                  {Math.round(row.Total)}
                </td>
              </>
            )}
          </tr>
        ))}
      </tbody>
    </table>
  );

  return (
    <div>
      <h1>Performance Summary</h1>
      <div>
        <button onClick={() => setView('daily')}>Daily Summary</button>
        <button onClick={() => setView('weekly')}>Weekly Summary</button>
        <button onClick={() => setView('all')}>All Summary</button>
      </div>
      {view === 'daily' ? (
        <>
          <h2>Daily Summary</h2>
          {renderTable(summaryData, false, false)}
        </>
      ) : view === 'weekly' ? (
        <>
          <h2>Weekly Summary</h2>
          {renderTable(weeklySummaryData, true, false)}
        </>
      ) : (
        <>
          <h2>All Summary</h2>
          {renderTable(allSummaryData, false, true)}
        </>
      )}
    </div>
  );
};

export default GoogleSheetData;
